import css from 'styled-jsx/css'
import theme from 'components/global-styles/theme'

export default css`
    /* language start*/
    .dropdown-lang {
        cursor: pointer;
        background: ${theme.colors['vida-white']};
        border: 1px solid ${theme.colors['vida-brokenwhite']};
        box-sizing: border-box;
        border-radius: 6px;
        padding: 4px 16px 4px 4px;
        font-weight: 600;
        font-size: 13px;
        line-height: 1.54;
        margin-right: 36px;
    }

    .dropdown-lang .dropdown:before {
        border: 2px solid #000000;
        border-radius: 1px;
        border-right: 0;
        border-top: 0;
        content: ' ';
        display: block;
        height: 6px;
        width: 6px;
        margin-top: -3px;
        pointer-events: none;
        position: absolute;
        top: 50%;
        right: -15px;
        transform: rotate(-45deg);
        transform-origin: center;
    }

    .navbar .dropdown-lang i {
        background-image: url(data:image/png;base64,);
        background-size: contain;
        width: 18px;
        height: 18px;
        display: block;
        margin-left: 5px;
    }

    .dropdown-lang .dropdown {
        margin-right: 20px;
        align-items: center;
    }

    .dropdown-lang .dropdown-menu {
        min-width: 155px;
        bottom: 0;
        top: auto;
        padding: 0;
    }

    .dropdown-lang .dropdown-content {
        box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.16);
        padding: 0;
        border-radius: 9px;
    }

    .dropdown-lang a.dropdown-item {
        display: flex;
        align-items: center;
        padding: 14px 21px;
    }

    .dropdown-lang a.dropdown-item:last-child {
        box-shadow: inset 0px 1px 0px rgba(0, 0, 0, 0.2);
    }

    .dropdown-lang a.dropdown-item:hover {
        background-color: transparent;
        color: ${theme.colors['vida-green']};
    }

    .dropdown-lang a.dropdown-item:hover span {
        color: ${theme.colors['vida-green']};
    }

    .dropdown-lang a.dropdown-item span {
        padding-left: 6px;
        font-size: 14px;
        line-height: 1.2;
        color: ${theme.colors['vida-gray']};
        text-transform: uppercase;
    }

    .dropdown-lang a.dropdown-item.is-active {
        background-color: transparent;
        color: #000000;
    }

    .dropdown-lang a.dropdown-item.is-active .checkmark {
        display: block;
        margin-left: auto;
    }

    .dropdown-lang-mobile > span {
        padding: 0 10px 0 0;
    }

    .dropdown-lang-mobile > span:after {
        content: '|';
        padding: 0 0 0 10px;
        color: ${theme.colors.black};
        font-size: 18px;
    }

    .dropdown-lang-mobile .is-active {
        color: #b9a05e;
    }

    .dropdown-lang-mobile > span:last-child:after {
        content: '';
    }

    .selected-lang {
        padding-left: 6px;
    }
    /* language end*/

    @media screen and (max-width: 1024px) {
        /* language start*/
        .dropdown-lang.is-mobile {
            margin: 0 24px;
            padding: 14px;
            box-shadow: inset 0px 1px 0px rgba(0, 0, 0, 0.2);
        }

        .dropdown-lang.is-mobile .dropdown {
            width: 100%;
        }

        .dropdown-lang.is-mobile .dropdown:before {
            right: 0;
            height: 8px;
            width: 8px;
        }
        .dropdown-lang.is-mobile .dropdown-menu {
            width: 100%;
            top: -8px;
        }

        .dropdown-lang.is-mobile .selected-lang {
            font-size: 18px;
            line-height: 1.2;
            color: #000000;
        }
    }

    @media screen and (max-width: 320px) {
        .dropdown-lang.is-mobile {
            font-size: 15px;
        }
    }
`
