import css from 'styled-jsx/css'
import theme from '../global-styles/theme'

export default css`
    .navbar {
        font-family: 'Bree Serif', serif;
        font-size: 14px;
        line-height: 1.43;
        background-color: #ffffff;
        color: ${theme.colors.black};
        height: ${theme.header.height};
        padding: 0 150px;
        position: fixed;
        width: 100%;
        top: 0;
        left: 0;
        min-height: 0;
    }

    .navbar.scroll-down {
        transform: translate(0, -82px);
        transition: transform 0.3s ease-in-out;
    }

    .navbar.scroll-up {
        transform: translate(0, 0);
        transition: transform 0.3s ease-in-out;
    }

    .navbar-item {
        color: ${theme.colors.black};
        padding: 0;
        margin: 0 30px;
        text-transform: uppercase;
    }

    a.navbar-item i.caret-down {
        //background-image: url(data:image/png;base64,);
        background-image: url(data:image/png;base64,);
        background-size: contain;
        background-repeat: no-repeat;
        position: absolute;
        right: -20px;
        top: 45%;
        display: block;
        width: 10px;
        height: 10px;
    }

    a.navbar-item span {
        z-index: 2;
    }

    a.navbar-item:hover,
    a.navbar-item:focus,
    a.navbar-item:active {
        background-color: transparent;
    }

    a.navbar-item:hover:after {
        content: '';
        position: absolute;
        top: 65%;
        display: block;
        width: 100%;
        height: 1px;
        background: #ffffff;
    }

    .navbar .navbar-item.is-active {
        color: #ffffff;
    }

    .navbar a.navbar-item:hover:after {
        content: '';
        position: absolute;
        top: 65%;
        display: block;
        width: 100%;
        height: 1px;
        background: ${theme.colors.black};
    }

    .navbar a.navbar-item.is-active:hover:after,
    .navbar .navbar-brand a.navbar-item:hover:after {
        display: none;
    }

    a.navbar-item.is-active:before {
        content: '';
        position: absolute;
        left: -29px;
        display: block;
        background-color: ${theme.header.activeBgColor};
        color: #ffffff;
        height: 36px;
        width: 100%;
        padding: 0 28px;
        box-sizing: content-box;
        border-radius: 60px;
        z-index: 1;
    }

    .navbar-brand a.navbar-item img {
        height: 44px;
        max-height: none;
    }

    .navbar-brand a.navbar-item:hover:after {
        display: none;
    }

    .navbar-menu.is-active .navbar-item {
        color: ${theme.colors.black};
        background-color: transparent;
        margin: 15px 30px;
    }

    .navbar-menu.is-active .navbar-lang {
        display: flex;
        align-items: center;
    }

    .navbar-menu.is-active .navbar-item.navbar-lang i {
        display: inline-block;
    }

    .navbar-menu.is-active .navbar-dropdown .navbar-item:hover:after,
    .navbar-menu.is-active a.navbar-item.is-active:before,
    .navbar-menu.is-active a.navbar-item:hover:after {
        display: none;
    }

    .navbar .navbar-menu.is-active .navbar-item.is-active {
        color: ${theme.colors.black};
    }

    .navbar-menu.is-active .navbar-item.has-dropdown {
        margin: 10px 30px 10px 50px;
    }

    .navbar-menu.is-active .navbar-item.has-dropdown .navbar-link {
        margin: 0;
        padding: 0;
        color: ${theme.colors.black};
    }

    .navbar-link.is-active,
    .navbar-link:focus,
    .navbar-link:focus-within,
    .navbar-link:hover,
    .navbar-item.is-active,
    .navbar-item:focus,
    .navbar-item:focus-within,
    .navbar-item:hover {
        background-color: transparent;
        color: inherit;
    }

    .navbar .navbar-lang:hover:after {
        display: none;
    }

    .navbar-menu.is-active .navbar-link:not(.is-arrowless)::after {
        border-color: ${theme.colors.black};
        border-width: 2px;
    }

    .navbar-menu.is-active .navbar-item.has-dropdown .navbar-dropdown {
        margin: 0;
        padding: 0;
    }

    .navbar-lang {
        cursor: pointer;
    }

    .navbar .navbar-lang i {
        background-image: url(data:image/png;base64,);
        background-size: contain;
        width: 18px;
        height: 18px;
        display: block;
        margin-left: 5px;
    }

    .navbar-lang .dropdown {
        align-items: center;
    }

    .navbar-lang .dropdown-menu {
        min-width: 132px;
    }

    .navbar-lang .dropdown-content {
        box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.16);
    }

    .navbar-lang a.dropdown-item.is-active {
        background-color: ${theme.colors.lightGold};
    }

    .navbar-lang-mobile > span {
        padding: 0 10px 0 0;
    }

    .navbar-lang-mobile > span:after {
        content: '|';
        padding: 0 0 0 10px;
        color: ${theme.colors.black};
        font-size: 18px;
    }

    .navbar-lang-mobile .is-active {
        color: #b9a05e;
    }

    .navbar-lang-mobile > span:last-child:after {
        content: '';
    }

    @media screen and (max-width: 1024px) {
        .navbar-burger {
            color: ${theme.colors.black};
            background-color: transparent;
            height: ${theme.header.height};
            margin-right: 12px;
        }

        .navbar-burger span {
            height: 2px;
            width: 24px;
        }

        .navbar-burger span:nth-child(1) {
            top: calc(50% - 7px);
        }

        .navbar-burger span:nth-child(2) {
            top: calc(50% - 1px);
        }

        .navbar-burger span:nth-child(3) {
            top: calc(50% + 5px);
        }

        .navbar-burger.is-active span:nth-child(3) {
            transform: translateY(-7px) rotate(-45deg);
        }
    }

    @media screen and (max-width: 768px) {
        .navbar {
            padding: 0;
        }
        .navbar-brand,
        .navbar-tabs {
            min-height: ${theme.header.height};
        }

        .navbar-item {
            margin: 0;
            margin-left: 24px;
        }

        .navbar-brand a.navbar-item img {
            height: 36px;
        }
    }
`
